package topic01;

public class LongType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		long value;
		value = 15165456546544165L;
		System.out.println("Result:\t"+value +"\n\n");
		
		int days =32768;
		System.out.println("Result:\t"+days +"\n\n");
		
		int val1=20;
		System.out.println("decimal:\t"+ val1 +"\n");
		
		val1 = 0x30;
		System.out.println("0x16:\t\t"+ val1 +"\n");
		
		val1 = 040;
		System.out.println("0x8:\t\t"+ val1 +"\n");
		
		float val2=20.1f;
		System.out.println("float value:\t"+val2);
		
		
	}

}
