package topic02;

public class ex04loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=101; i<106 ; i++) {
			System.out.println("value of i: \t"+i);
		}
		
		int j=202;
		while(j<215) {
			System.out.println("value of jj: \t"+j);
			j++;
		}

	}

}
